from rich.console import Console
from rich.table import Table

console = Console()


def print_area_table(area_data):
    table = Table(title="Area Data")
    table.add_column("Key")
    table.add_column("Value")

    for key, value in area_data.items():
        table.add_row(key, str(value))

    console.print(table)


def print_coordinates_table(coordinates_data):
    table = Table(title="Coordinates Data")
    table.add_column("Index", style="bold")
    table.add_column("Coordinate and Area")

    for i, item in enumerate(coordinates_data):
        coord_table = Table(box=None)
        coord_table.add_column("Key", style="bold")
        coord_table.add_column("Value")

        coordinate = item.get('coordinate', {})
        area = item.get('area', {})

        for key, value in coordinate.items():
            coord_table.add_row(key, str(value))

        area_table = Table(box=None)
        area_table.add_column("Key", style="bold")
        area_table.add_column("Value")

        for key, value in area.items():
            area_table.add_row(key, str(value))

        combined_table = Table(box=None)
        combined_table.add_row(coord_table)
        combined_table.add_row(area_table)

        table.add_row(str(i), combined_table)

    console.print(table)
